void loading(void){
    for(int i = 4; i<113; i+=3){
        loading_var=i;
        draw_loading();
        delay(10);
    }
}
void countdown(uint8_t T_sec, uint8_t P_STime, uint8_t P_DTime)
{
    TickType_t lastWakeTime = xTaskGetTickCount();
    for (int j = T_sec; j > 0; j--)
    {
        itoa(j, countdown_var, 10);

        if (j <= P_STime && j > P_STime-P_DTime) {
            draw_launch_preheat();
        } else {
            draw_launch();
        }

        vTaskDelayUntil(&lastWakeTime, pdMS_TO_TICKS(1000));
    }
}


